import{_ as t,o as e,c as l,a2 as i}from"./chunks/framework.DT_KUyqE.js";const p=JSON.parse('{"title":"更换杭州市民卡银行流程","description":"","frontmatter":{"date":"2024-04-27T00:00:00.000Z","abstract":"2024 年起许多杭州政府的补贴都将发放至市民卡的金融账户。而我原先市民卡的银行账户我不是很喜欢，决定去申请更换。","tags":["杭漂"]},"headers":[],"relativePath":"posts/生活经验/更换杭州市民卡银行流程.md","filePath":"posts/生活经验/更换杭州市民卡银行流程.md","lastUpdated":1751712693000}'),o={name:"posts/生活经验/更换杭州市民卡银行流程.md"};function r(s,a,n,d,c,_){return e(),l("div",null,a[0]||(a[0]=[i('<h1 id="更换杭州市民卡银行流程" tabindex="-1">更换杭州市民卡银行流程 <a class="header-anchor" href="#更换杭州市民卡银行流程" aria-label="Permalink to &quot;更换杭州市民卡银行流程&quot;">​</a></h1><h2 id="具体流程" tabindex="-1">具体流程 <a class="header-anchor" href="#具体流程" aria-label="Permalink to &quot;具体流程&quot;">​</a></h2><ol><li>去现市民卡银行注销借记卡，并打印注销凭证。</li><li>申请补还市民卡，可通过市民卡 APP 申请，也可直接前往市民卡服务网点（我前往的是市民卡体验中心，即去即领）。</li><li>补还完成，拿到实体卡之后，去对应的银行激活金融账户（也就是办理开户业务），这一步完成后银行卡账户已经可以使用了。</li><li>最后在市民卡 APP 的市民卡服务中点击激活按钮，这一步是为了在市民卡 APP 中去消费金融账户余额。</li></ol><h2 id="首次激活三代卡福利" tabindex="-1">首次激活三代卡福利 <a class="header-anchor" href="#首次激活三代卡福利" aria-label="Permalink to &quot;首次激活三代卡福利&quot;">​</a></h2><ul><li>不同的银行办理市民卡账户激活后会有不同福利，我办理的是工行，获取了 50 元立减金。</li><li>市民卡 APP 的市民卡服务中也有相应福利。</li></ul>',5)]))}const u=t(o,[["render",r]]);export{p as __pageData,u as default};
